/*自动获取搜索地址和参数 
11.19 by: youke2580 
备用域名：
https://m.oldtimeswx.cc
https://www.oldtimeswx.cc
https://www.oldtimesxs.cc
https://www.oldtimescc.com
https://www.lanrenyuhai.com
以下代码勿动！*/
var u=source.getKey(),
v=source.getVariable(),
key=java.get("key"),
set=(url,body)=>{
	java.log("set 保存中…")
	obj={url:url,body:body}
	str=JSON.stringify(obj)
	source.setVariable(str)
	};
if(!v||v==""){
	var html=java.ajax(u),so=[],
	j=org.jsoup.Jsoup.parse(html);
	a=j.select("form").get(0).attr("action");
	f=j.select("form").get(1).select("input");
	java.log("init 初始化执行中…")
	for(i in f){
		name=f[i].attr("name")
		value=f[i].attr("value")
		type=f[i].attr("type")
		if(type=="text"){
			add=`${name}={{key}}`;
			}else{
				add=`${name}=${value}`
				}
			so.push(add)
		}
		sk=so.join("&")
		//java.log(sk)
		set(a,sk)
		s=a;
		body=sk;
	}else{
		$=JSON.parse(v)
		body=$.body
		s=$.url;
		}
body=body.replace("{{key}}",key);
s;
body;

