api_Urlhost = "http://qyapi.kuduwxw.com";
api_Params = {
    "product": "1",
    "ver": "3.9.3",
    "marketChannel": "none",
    "sysVer": "13",
    "token": "",
    "appId": "",
    "osType": "2",
    "time": ~~(new Date() / 1e3),
    "packageName": "com.qingyuletu.app",
    "udid": java.randomUUID()
};
function sign(body){
		sb = `i3aphc4zg3hmmcq0${body}8je1sc1htum1hvutpso79oigrx8pv2gx`;
		return java.md5Encode(sb).toUpperCase();
	}
function body($){
		$ = Object.assign(api_Params, $);
		return Object.keys($).sort()
		.map(key=>key+"="+$[key]).join('&');
	}
api_Params.sign=sign(body(link_Params));
ajax_url = api_Urlhost + "/" + link_Urlpath + "," + JSON.stringify({
		"method": "POST",
		"body": api_Params
	});

